cordova = new Cordova();

// Code taken from MatthewCrumley (http://stackoverflow.com/a/934925/298479)
var getBase64Image = function(img) {
  // Create an empty canvas element
  var canvas = document.createElement("canvas");
  canvas.width = img.width;
  canvas.height = img.height;

  // Copy the image contents to the canvas
  var ctx = canvas.getContext("2d");
  ctx.drawImage(img, 0, 0);

  // Get the data-URL formatted image
  // Firefox supports PNG and JPEG. You could check img.src to guess the
  // original format, but be aware the using "image/jpg" will re-encode the image.
  var dataURL = canvas.toDataURL("image/jpg");

  return dataURL.replace(/^data:image\/(png|jpg);base64,/, "");
}

capturePhoto = function(id) {
  function onSuccess(imageURI) {
    var image = document.getElementById(id);
    image.src = "/img/tox-logo-test.jpg";
  }

  function onFail(message) {
    setTimeout(function() {
      alert('Failed because: ' + message);
    }, 0);
  }

  cordova.call('navigator.camera.getPicture', [onSuccess, onFail,
    { quality: 40,
      targetWidth: 200,
      targetHeight: 200,
      saveToPhotoAlbum: false,
      destinationType: cordova.call('navigator.camera.DestinationType.FILE_URI'),
      sourceType: cordova.call('navigator.camera.PictureSourceType.CAMERA'),
      encodingType: cordova.call('navigator.camera.EncodingType.JPEG')
    }
  ]);

  var image = document.getElementById(id);
  image.alt = ""

  image.src = "data:image/jpg;base64," + image.alt;
}